$(function(){$("html").swipe({swipe:function(c,e,f,d,a,b){if(e=="left"){window.location.href="?d="+$("head").data("nextday")}if(e=="right"){window.location.href="?d="+$("head").data("prevday")}},threshold:75});$(document).keydown(function(a){switch(a.which){case 37:window.location.href="?d="+$("head").data("prevday");break;case 39:window.location.href="?d="+$("head").data("nextday");break;case 38:window.location.href="?d="+$("head").data("prevweek");break;case 40:window.location.href="?d="+$("head").data("nextweek");break;case 13:window.location.href=".";break;default:return}a.preventDefault()})});