$(function(){$("html").swipe({swipe:function(c,e,f,d,a,b){if(e=="left"){window.location.href="?d="+$("head").data("nextday")}if(e=="right"){if($("head").data("prevday")!=="none"){window.location.href="?d="+$("head").data("prevday")}}},threshold:75});$(document).keydown(function(a){switch(a.which){case 37:case 65:if($("head").data("prevday")!=="none"){window.location.href="?d="+$("head").data("prevday")}break;case 39:case 68:window.location.href="?d="+$("head").data("nextday");break;case 38:case 87:if($("head").data("prevweek")!=="none"){window.location.href="?d="+$("head").data("prevweek")}break;case 40:case 83:window.location.href="?d="+$("head").data("nextweek");break;case 13:window.location.href=".";break;default:return}a.preventDefault()})});