$(function(){$("html").swipe({swipe:function(c,e,f,d,a,b){if(e=="left"){window.location.href="?d="+$("head").data("nextday")}if(e=="right"){if($("head").data("prevday")!=="none"){window.location.href="?d="+$("head").data("prevday")}}},threshold:10});$(document).keydown(function(a){switch(a.which){case 65:case 72:if($("head").data("prevday")!=="none"){window.location.href="?d="+$("head").data("prevday")}break;case 68:case 76:window.location.href="?d="+$("head").data("nextday");break;case 87:case 75:window.location.href="?d="+$("head").data("nextweek");break;case 83:case 74:if($("head").data("prevweek")!=="none"){window.location.href="?d="+$("head").data("prevweek")}break;case 13:if($("head").data("today")!==$("head").data("desireddate")){window.location.href="."}break;default:return}a.preventDefault()})});